$(document).ready(function(){$('#fileupload').fileupload({url:'../../../app-assets/jquery-file-upload/server/php/',disableImageResize:/Android(?!.*Chrome)|Opera/.test(window.navigator.userAgent),autoUpload:false,maxFileSize:999000,acceptFileTypes:/(\.|\/)(gif|jpe?g|png)$/i});$('#fileupload').fileupload('option','redirect',window.location.href.replace(/\/[^\/]*$/,'../../../app-assets/jquery-file-upload/cors/result.html?%s'));if($.support.cors){$.ajax({type:'HEAD'}).fail(function(){$('<div class="alert alert-danger"/>').text('Upload server currently unavailable - '+
new Date()).appendTo('#fileupload');});}
$('#fileupload').addClass('fileupload-processing');$.ajax({url:$('#fileupload').fileupload('option','url'),dataType:'json',context:$('#fileupload')[0]}).always(function(){$(this).removeClass('fileupload-processing');}).done(function(result){$(this).fileupload('option','done').call(this,$.Event('done'),{result:result});});});