$(document).ready(function(){$(".accordion-default").accordion();$(".accordion-collapsible").accordion({collapsible:true});$(".accordion-height").accordion({heightStyle:"content"});$(".accordion-sortable").accordion({collapsible:true,heightStyle:"content",header:"> div > h3"}).sortable({axis:"y",handle:"h3",stop:function(event,ui){ui.item.children("h3").triggerHandler("focusout");$(this).accordion("refresh");}});$(".menu-default, .disabled-menu-items").menu();$(".menu-disabled").menu({disabled:true});$(".menu-category-header").menu({items:"> :not(.ui-widget-header)"});$(".menu-icons").menu({items:"> :not(.ui-widget-header)"});$(".menu-header-icons").menu({items:"> :not(.ui-widget-header)"});$(".tabs-default").tabs();$(".tabs-collapse").tabs({collapsible:true});$(".tabs-mouseover").tabs({event:"mouseover"});var tabs=$(".tabs-sortable").tabs();tabs.find(".ui-tabs-nav").sortable({axis:"x",stop:function(){tabs.tabs("refresh");}});});